import { Component, OnInit } from '@angular/core';
import { AdminRegisterService } from '../service/admin-register.service';
import { User } from '../service/user1';



@Component({
  selector: 'app-admin-table',
  templateUrl: './admin-table.component.html',
  styleUrls: ['./admin-table.component.scss'],
  providers:[AdminRegisterService]
})
export class AdminTableComponent implements OnInit {
  admins:any=[];

  visible!:boolean;
  constructor(protected adminService:AdminRegisterService) { }
  async ngOnInit(): void {

    this.adminList();
    console.log("Oh hit "+this.adminService.admins);

  }

  adminList(){
    this.adminService.getAdmin().subscribe((data)=>{
      // console.log("Data"+data)
      const val = JSON.stringify(data)
      console.log(val)
      this.admins = val;
      this.adminService.admins = this.admins;
      //  console.log(typeof(this.adminService.admins));
      console.log("this admin "+this.admins);



      if(data!=0){
        this.visible=false;
      }
      else{
        this.visible=true;
      }
    })
  }
}
